import java.util.Scanner;

public class OneA {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int i1=sc.nextInt();
		if(i1==0)
		{
			System.out.println("Zero");
		}
		else if(i1>0)
		{
			System.out.println("Positive");
		}
		else
		{
			System.out.println("Negative");
		}
		
		

	}

}
