import java.util.Scanner;

public class oneh {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int N=sc.nextInt();
		if(N==0)
		{
			System.out.println("Zero");
		}
		else if(N>0)
		{
			System.out.println("Positive");
		}
		else
		{
			System.out.println("Negative");
		}
		
		

	}

}
